import { Component, Input } from '@angular/core';
import { FormGroup, FormArray, FormControl, FormBuilder} from '@angular/forms';

@Component({
  selector: 'app-songslistcomponent',
  templateUrl: './songslistcomponent.component.html',
  styleUrls: ['./songslistcomponent.component.css']
})
export class SongslistcomponentComponent {
songs_title = 'Add Songs To Your Playlist  ';
enter_new_song = 'Enter new song';
playlist_button = 'Add to Playlist';
song_button='Add Song';
isChecked:boolean = false;
@Input()
counter: Number =0;
emailFormArray: Array<any> = [];
songs: any[]=[
  {name: 'song1',},
  {name: 'song2',},
  {name: 'song3',},
  {name: 'song4',},
];
enable_button(){
this.isChecked = !this.isChecked;
}
add_song_to_array(item: any){
  this.songs.push({name:item});
  this.counter = this.songs.length;
}
onChange(song :string, isChecked: boolean) {
  if(isChecked) {
    this.emailFormArray.push(song);
    this.isChecked = !this.isChecked;
    console.log(this.isChecked);
    console.log(this.emailFormArray)
  } else {
    let index = this.emailFormArray.indexOf(song);
    this.emailFormArray.splice(index,1);
  }
}
}
