import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-songslistcomponent',
  templateUrl: './songslistcomponent.component.html',
  styleUrls: ['./songslistcomponent.component.css']
})
export class SongslistcomponentComponent {
songs_title: string = 'Add Songs To Your Playlist  ';
enter_new_song = 'Enter new song';
playlist_button = 'Add to Playlist';
song_button='Add Song';
public isChecked:boolean = false;
@Input()
songscounter: Number =0;

songs =[
  {name: 'song1',id: 1,selected: false},
  {name: 'song2',id: 2,selected: false},
  {name: 'song3',id: 3,selected: false},
  {name: 'song4',id: 4,selected: false},
];
enable_button(){
this.isChecked = !this.isChecked;
}

@Output() addSong: EventEmitter<number> = new EventEmitter<number>();
add_song_to_array(item: any){
  this.songs.push({name:item,id:item,selected:item});
  this.addSong.emit(this.songs.length);
}
@Output() addPlaylist: EventEmitter<string> = new EventEmitter<string>();
add_song_to_playlist(item: any){
  for ( let i=0; i<this.songs.length; i++)
  this.songs[i].selected = false;
  this.songs[item.id -1].selected = true;
  this.addPlaylist.emit(item);
}

}
